import React,{ useState } from 'react'

export default function Color(){

// const [bodyColor, setBodyColor] = useState({
// 	// color: "black",
// 	// bodyColor: " "

// })
// const [btnText, setBtnText] = useState("Dark Mode")
const toggleStyle =(id)=>{
	document.body.style.background = document.getElementById(id)
}
return (
	<div className="container">
		<button onClick={toggleStyle} id = "a" type="button" class="btn btn-primary">{toggleStyle}</button>
		{/* <button onClick={toggleStyle} id = "b" type="button" class="btn btn-secondary">{btnText}</button>
		<button onClick={toggleStyle} id = "c" type="button" class="btn btn-success">{btnText}</button>
		<button onClick={toggleStyle} id = "d" type="button" class="btn btn-danger">{btnText}</button> */}
		<button onClick={toggleStyle} id = "e" type="button" class="btn btn-warning">Warning</button>
		<button onClick={toggleStyle} id = "f" type="button" class="btn btn-info">Info</button>
		<button onClick={toggleStyle} id = "g" type="button" class="btn btn-light">Light</button>
		<button onClick={toggleStyle} id = "h" type="button" class="btn btn-dark">Dark</button>
		<button onClick={toggleStyle} id = "i" type="button" class="btn btn-link">Link</button>
	</div>
)
}
