import React,{ useState } from 'react'

export default function Color(){

const [bodyColor, setBodyColor] = useState({
	// color: "black",
	// bodyColor: " "

})
const [btnText, setBtnText] = useState("Dark Mode")
const toggleStyle =()=>{
	if(bodyColor.color === 'black'){
		setBodyColor({
			color: 'white',
			backgroundColor:'black',
			border: '1px solid white',
		})
		// setBtnText("Light Mode")
	}
	else{
		setBodyColor({
			color: 'black',
			backgroundColor:'white' 
		})
		// setBtnText("Dark Mode")
	}
}

return (
	<div className="container">
		<button onClick={toggleStyle} type="button" class="btn btn-primary">{btnText}</button>
		<button onClick={toggleStyle} type="button" class="btn btn-secondary">{btnText}</button>
		<button onClick={toggleStyle} type="button" class="btn btn-success">{btnText}</button>
		<button onClick={toggleStyle} type="button" class="btn btn-danger">{btnText}</button>
		<button onClick={toggleStyle} type="button" class="btn btn-warning">Warning</button>
		<button onClick={toggleStyle} type="button" class="btn btn-info">Info</button>
		<button onClick={toggleStyle} type="button" class="btn btn-light">Light</button>
		<button onClick={toggleStyle} type="button" class="btn btn-dark">Dark</button>
		<button onClick={toggleStyle} type="button" class="btn btn-link">Link</button>
	</div>
   )
}
