import React, {useState} from 'react'

export default function TextForm(props) {
	const handleUpClick = () =>{
		// console.log("Uppercase Was Clicked"+ text);
		let newText =text.toUpperCase();
		setText(newText);
		props.showAlert("Converted to uppercase !","success")
	}
	const handleLoClick = () =>{
		let newText =text.toLowerCase();
		setText(newText);
		props.showAlert("Converted to lowercase !","success")
	}
	const handleClearClick = () =>{
		let newText ='';
		setText(newText);
		props.showAlert("Text Cleared !","success")
	}
	const handleCopy = ()=>{
		// var text = document.getElementById("myBox");
		// text.select();
		navigator.clipboard.writeText(text);
		// document.getSelection().removeAllRanges();
		props.showAlert("Copied to Clipboard !","success")
	}

	const handleTitleClick = () => {
		let newText = text.replace(/\w\S*/g,function(t){
			return t.charAt(0).toUpperCase() + t.slice(1);

		})
		setText(newText);
		props.showAlert("Converted to camelcase","success")
	}
	const handleExtraSpaces = () =>{
		let newText = text.split(/[ ]+/);
		setText(newText.join(" "))
		props.showAlert("Extra Spaces Removed","success")
	}

	const handleOnChange = (event) =>{
		// console.log("On Change");
		setText(event.target.value);	
	}
	const [text, setText] = useState('');
	return (
		<>
	<div className="container" style ={{color: props.mode==='dark'?'white':'#042743'}}>
		<h1>{props.heading}</h1>	
		<div className="mb-3">
	 		<textarea className="form-control" value={text} onChange ={handleOnChange} style ={{backgroundColor: props.mode==='dark'?'rgb(36 74 104)':'white',color: props.mode==='dark'?'white':'#042743'}} id="myBox" rows="8"></textarea>
		</div>
		<button className = "btn btn-primary mx-2 my-2" onClick={handleUpClick}>Convert To Uppercase</button>
		<button className = "btn btn-primary mx-2 my-2" onClick={handleLoClick}>Convert To Lowercase</button>
		<button className = "btn btn-primary mx-2 my-2" onClick={handleClearClick}>Clear Text</button>
		<button className = "btn btn-primary mx-2 my-2" onClick={handleCopy}>Copy Text</button>
		<button className = "btn btn-primary mx-2 my-2" onClick={handleTitleClick}>Convert To Camelcase</button>
		<button className = "btn btn-primary mx-2 my-2" onClick={handleExtraSpaces}>Remove Extra Spaces</button>
	</div>
	<div className="container my-2"style ={{color:props.mode==='dark'?'white':'#042743'}}>
		<h2>Your Text Summary</h2>
		<p>{text.split(/\s+/).filter((element)=>{return element.length!=0}).length} Words and {text.length} Characters</p>
		<p>{0.008 * text.split(" ").filter((element)=>{return element.length!=0}).length} Minutes Read</p>
		<h2>Preview</h2>
		<p>{text.length>0?text:"Enter something in the textbox above to preview it here"}</p>
	</div>
	</>
  )
}
