import './App.css';
// import About from './components/About';
import Navbar from './components/Navbar';
import TextForm from './components/TextForm';
import react, {useState} from 'react'
import Alert from './components/Alert';
// import Color from './components/Color';


function App() {
    const [alert, setAlert] = useState(null)

    const showAlert = (message,type) => {
      setAlert({
      msg: message,
      type: type
    })
    setTimeout(() => {
      setAlert(null);
    },2000)
  }

    const [mode, setMode] = useState('light')


    const toggleMode = () => {
      if(mode === 'light'){
      setMode('dark');
      document.body.style.backgroundColor = '#042743';
      showAlert("Dark mode has been enable","success");
      document.props.text= 'Dark Mode'
    }
    else{
      setMode('light');
      document.body.style.backgroundColor = 'white';
      showAlert("Light mode has been enable","success");
      document.props.text = 'Light Mode'

    } 
  }
  const myMode = () => {
    if (mode ==='light'){
      document.text = 'Dark Mode'
    }
    else{
      document.text = 'Light Mode'
    }

  }
    return (
      <>
        {/* <Navbar title ="My First App" aboutText ="About Us" /> */}
      {/* <Navbar/> */}
      <Navbar title ="My First App" mode= {mode} toggleMode={toggleMode} myMode ={myMode}/>
      {/* <Navbar title ="My First App" /> */}
      <Alert alert= {alert}/>
      <div className="container my-3">
      <TextForm  showAlert ={showAlert} heading = "Enter The Text To Analyze Below"mode= {mode} /> 
      {/* <Color/> */}
      {/* <About/> */}
      </div>

      </>
  );
}

export default App;
